visitScheduleRule:
"use strict";
({ params, imports }) => {
  const programEncounter = params.entity;
  const scheduleBuilder = new imports.rulesConfig.VisitScheduleBuilder({
    programEncounter
  });
//Date and time when placenta was out
const dateOfDelivery = programEncounter.getObservationValue('Date and time when placenta was out');
  scheduleBuilder
    .add({
      name: "PNC 1",
      encounterType: "Mother PNC",
      earliestDate: programEncounter.encounterDateTime,
      maxDate: imports.moment(programEncounter.encounterDateTime).add(3, 'days').toDate()
    }).whenItem( imports.moment(programEncounter.encounterDateTime).isBefore(imports.moment(dateOfDelivery).add(42, 'days'), 'date'))
    .is.truthy;
  return scheduleBuilder.getAll();
};
