decisionRule:
"use strict";
({params, imports}) => {
    const programEncounter = params.entity;
    const decisions = params.decisions;
    const complicationsBuilder = new imports.rulesConfig.complicationsBuilder({
        programEncounter: programEncounter,
        complicationsConcept: "High Risk Conditions"
    });

    const recommendationsBuilder = new imports.rulesConfig.complicationsBuilder({
        programEncounter: programEncounter,
        complicationsConcept: "Recommendations"
    });

     complicationsBuilder
        .addComplication("Rh negative blood group")
        .when.valueInEncounter("Blood group")
        .containsAnyAnswerConceptName("AB-", "O-", "A-", "B-");
     complicationsBuilder
        .addComplication("HIV (Elisa) is positive")
        .when.valueInEncounter("HIV (Elisa)")
        .containsAnswerConceptName("Positive");
     complicationsBuilder
        .addComplication("Hb Electrophoresis")
        .when.valueInEncounter("Hb Electrophoresis")
        .containsAnswerConceptName("SS");
     complicationsBuilder
        .addComplication("Hepatitis B is positive")
        .when.valueInEncounter("Hepatitis B")
        .containsAnswerConceptName("Positive");
     complicationsBuilder
        .addComplication("VDRL is positive")
        .when.valueInEncounter("VDRL")
        .containsAnswerConceptName("Positive");

        //High Blood Sugar 
      complicationsBuilder
        .addComplication("High Blood Sugar")        
        .when.valueInEncounter('Random Blood Sugar (RBS)').is.greaterThan(160);

      complicationsBuilder
        .addComplication("High Glucose Level")        
        .when.valueInEncounter('Glucose test (75gm Glucose)').is.greaterThan(140);

               //Since RBS is high, refer to subcenter for FBS, PP2BS and HbA1C
       recommendationsBuilder
        .addComplication("Since RBS is high, refer to subcenter for FBS, PP2BS and HbA1C")        
        .when.valueInEncounter('Random Blood Sugar (RBS)').is.greaterThan(160);



    decisions.encounterDecisions.push(complicationsBuilder.getComplications());
    decisions.encounterDecisions.push(recommendationsBuilder.getComplications());
    return decisions;
};
