visitScheduleRule:
"use strict";
({ params, imports }) => {
  const programEncounter = params.entity;
  const moment = imports.moment;
  const scheduleBuilder = new imports.rulesConfig.VisitScheduleBuilder({programEncounter});

  const condition11 = new imports.rulesConfig.RuleCondition({programEncounter}).when.valueInEncounter("1a5c7c0f-40e2-452a-b38a-449b554a3747").defined.matches();

  if(condition11 ){
    const earliestDate = moment(programEncounter.encounterDateTime).add(0, 'days').toDate();
    const maxDate = moment(programEncounter.encounterDateTime).add(0, 'days').toDate();
    scheduleBuilder.add({name: "TB Study Baseline Barrier and Social support assessment", encounterType: "TB Study Baseline Barrier and Social support assessment", earliestDate, maxDate});  
}

  return scheduleBuilder.getAll();
};
