({ params, imports }) => {
  const programEncounter = params.entity;
  const moment = imports.moment;
  const scheduleBuilder = new imports.rulesConfig.VisitScheduleBuilder({programEncounter});

  const condition11 = new imports.rulesConfig.RuleCondition({programEncounter}).when.valueInEncounter("3c8c05fb-7869-4986-9903-b3ac49db7fb2").equals(18).matches();

  if(condition11 ){
    const earliestDate = moment(programEncounter.earliestVisitDateTime).add(30, 'days').toDate();
    const maxDate = moment(programEncounter.earliestVisitDateTime).add(7, 'days').toDate();
    scheduleBuilder.add({name: "TB Study Followup", encounterType: "TB Study Followup", earliestDate, maxDate});  
}

  return scheduleBuilder.getAll();
};
