formElements.rule:
'use strict';
({params, imports}) => {
  const programEncounter = params.entity;
  const formElement = params.formElement;
  const statusBuilder = new 
  imports.rulesConfig.FormElementStatusBuilder({programEncounter, formElement});
  statusBuilder.show().when.valueInEncounter('Follow USG required').containsAnswerConceptName('Yes');

return statusBuilder.build();
};

visitScheduleRule:
"use strict";
({ params, imports }) => {
  const programEncounter = params.entity;
  const scheduleBuilder = new imports.rulesConfig.VisitScheduleBuilder({
    programEncounter
  });
const nextUSGDate = programEncounter.getObservationValue('Date of next USG');
  scheduleBuilder
    .add({
      name: "USG Report",
      encounterType: "USG Report",
      earliestDate: imports.moment(nextUSGDate).toDate(),
      maxDate: imports.moment(nextUSGDate).add(4, 'days').toDate()
    }).whenItem(!_.isEmpty(nextUSGDate))
    .is.not.truthy;
  return scheduleBuilder.getAll();
};
