enrolmentEligibilityCheckRule:
'use strict';
({params, imports}) => {
  const individual = params.entity;
  return individual.isFemale() && individual.getAgeInYears() > 15;
};

enrolmentSummaryRule:
'use strict';
({params, imports}) =>  {
    const summaries = [];
    const programEnrolment = params.programEnrolment;

   const lmp = programEnrolment.findObservationInEntireEnrolment('Last menstrual period').getReadableValue();
   if(lmp){
    var lmpDate = imports.moment(lmp).toDate();
    summaries.push({name: 'Last menstrual period', value: lmpDate});
   }

    const edd = programEnrolment.findObservationInEntireEnrolment('EDD').getReadableValue();;
     if (edd) {
     var eddDate = imports.moment(edd).toDate();
      summaries.push({name: 'EDD', value: eddDate});
    }

    //Weight
     const weight = programEnrolment.findObservationInEntireEnrolment('Weight');
    if (weight) {
      summaries.push({name: 'Weight', value: weight.getValue()});
    }

    //FHS
     const fhs = programEnrolment.findLatestObservationFromEncounters('FHS');
    if (fhs) {
      summaries.push({name: 'FHS', value: fhs.getValue()});
    }

    //Hb
      const Hb = programEnrolment.findLatestObservationFromEncounters('Hb');
    if (Hb) {
      summaries.push({name: 'Hb', value: Hb.getValue()});
    }

    //Urine Albumin
     const urineAlbumin = programEnrolment.findLatestObservationFromEncounters('Urine Albumin');
    if (urineAlbumin) {
      summaries.push({name: 'Urine Albumin', value: urineAlbumin.getValue()});
    }

    //High risk condition in previous pregnancy
    const highRiskPrevious = programEnrolment.findObservationInEntireEnrolment('High risk condition in previous pregnancy');
    if (highRiskPrevious) {
      summaries.push({name: 'High risk condition in previous pregnancy', value: highRiskPrevious.getValue()});
    }

   //High Risk Conditions
    const highRiskEnrolment  = programEnrolment.findObservation('High Risk Conditions').getValue();
    const highRiskLatest = programEnrolment.findLatestObservationFromEncounters('High Risk Conditions').getValue();
    if ( highRiskEnrolment || highRiskLatest) { 
      const highRisk = highRiskEnrolment.concat(highRiskLatest);
      summaries.push({name: 'High Risk Conditions', value: highRisk});
    } 


    return summaries;
};
